        i0.ɵɵtext(212, " \n                            def IndAnt(self, year : int, month : int, cod : str) -> None:\n\n                                query = f'SELECT a.{cod}Cod, a.{cod}Ind, b.{cod}Ind, c.{cod}Ind, d.{cod}Ind, e{cod}Ind, f.{cod}Ind, g{cod}Ind, h.{cod}Ind \\\n                                    FROM (SELECT {cod}Cod, {cod}Ind FROM DB1.dbo.INDICES{self.di[cod]} \\\n                                    WHERE YEAR = {year} AND MONTH = {month}) a \\\n                                    FULL OUTER JOIN (SELECT {cod}Cod, {cod}Ind FROM DB2.dbo.INDICES{self.di[cod]} \\\n                                    WHERE YEAR = {year} AND MONTH = {month}) b ON a.{cod{Cod = b.{cod}Cod\\\n                                    FULL OUTER JOIN (SELECT {cod}Cod, {cod}Ind FROM DB3.dbo.INDICES{self.di[cod]} \\\n                                    WHERE YEAR = {year} AND MONTH = {month}) c ON a.{cod}Cod = c.{cod}Cod\\\n                                    FULL OUTER JOIN (SELECT {cod}Cod, {cod}Ind FROM DB4.dbo.INDICES{self.di[cod]} WHERE YEAR = {year} AND MONTH = {month}) d ON a.{cod}Cod = d.{cod}Cod\\\n                                    FULL OUTER JOIN (SELECT {cod}Cod, {cod}Ind FROM DB5.dbo.INDICES{self.di[cod]} WHERE YEAR = {year} AND MONTH = {month}) e ON a.{cod}Cod = e.{cod}Cod\\\n                                    FULL OUTER JOIN (SELECT {cod}Cod, {cod}Ind FROM DB6.dbo.INDICES{self.di[cod]} WHERE YEAR = {year} AND MONTH = {month}) f ON a.{cod}Cod = f.{cod}Cod\\\n                                    FULL OUTER JOIN (SELECT {cod}Cod, {cod}Ind FROM DB7.dbo.INDICES{self.di[cod]} WHERE YEAR = {year} AND MONTH = {month}) g ON a.{cod}Cod = g.{cod}Cod\\\n                                    FULL OUTER JOIN (SELECT {cod}Cod, {cod}Ind FROM DB8.dbo.INDICES{self.di[cod]} WHERE YEAR = {year} AND MONTH = {month}) h ON a.{cod}Cod = h.{cod}Cod'\n                                self.indant = pd.read_sql(query, self.connect('master'), index_col=cod+'Cod')\n                            ");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(213, "\n                        ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(214, "button", 14);
        i0.ɵɵtext(215, " Copy ");
