        i0.ɵɵelementStart(230, "p");
        i0.ɵɵtext(231, "Finally create an instance of CPI, this takes credentials from console, called \"temp\" and execute builder method.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(232, "div", 11)(233, "div", 12)(234, "pre");
        i0.ɵɵtext(235, "                            ");
