import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-elastic-docker',
  templateUrl: './elastic-docker.component.html',
  styleUrls: ['./elastic-docker.component.css']
})
export class ElasticDockerComponent {

  tableOfContents: Array<string>[]= [
    ['section1','Docker compose image'],
    ['section2','Token and password for kibana']
  ];


  ngOnInit() {      
    this.loadScript('./assets/prism.js', 'js');
    window.onload = () => {
      this.loadScript('./assets/main.js', 'js');
    };
  }

  loadScript(scriptUrl: string,Tipo: string) {
    if (Tipo=='js'){
      const script = document.createElement('script');
      script.type = 'text/javascript';
      script.src = scriptUrl;
      document.body.appendChild(script);
    }  else if (Tipo === 'css') {
      const link = document.createElement('link');
      link.rel='stylesheet'
      link.type = 'text/css';
      link.href = scriptUrl;
      document.head.appendChild(link);
    }

  }

}
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta http-equiv="X-UA-Compatible" content="IE=edge">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">

    <title>Document</title>
</head>

<body>

    <app-header></app-header>

    <div class="global">
        <div class="toc">
                <app-table-of-contents [tableOfContents]="tableOfContents" (scrollToElement)="scrollToSection($event)"></app-table-of-contents>
        </div>        

        <div class="content">
                <div id="section1"></div>
                <div id="section2"></div>
        </div>

    </div>

</body>
